import React, { useState } from "react";

const LeagueInput = () => {
  const [leagueName, setLeagueName] = useState("");
  const [teams, setTeams] = useState([]);

  const handleLeagueNameChange = (e) => {
    setLeagueName(e.target.value);
  };

  const handleAddTeam = (e) => {
    e.preventDefault();
    const newTeam = {
      name: e.target.elements.team.value,
      score: parseInt(e.target.elements.score.value),
    };
    setTeams([...teams, newTeam]);
    e.target.reset();
  };

  const handleDeleteTeam = (index) => {
    setTeams([...teams.slice(0, index), ...teams.slice(index + 1)]);
  };

  return (
    <div className="LeagueInput">
      <form onSubmit={handleAddTeam}>
        <label>
          League Name:
          <input
            type="text"
            value={leagueName}
            onChange={handleLeagueNameChange}
          />
        </label>
        <br />
        <label>
          Team Name:
          <input type="text" name="team" />
        </label>
        <label>
          Score:
          <input type="number" name="score" />
        </label>
        <button type="submit">Add Team</button>
      </form>
      <h2>Teams:</h2>
      {teams.length === 0 ? (
        <p>No teams added yet.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Team Name</th>
              <th>Score</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {teams.map((team, index) => (
              <tr key={index}>
                <td>{team.name}</td>
                <td>{team.score}</td>
                <td>
                  <button type="button" onClick={() => handleDeleteTeam(index)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default LeagueInput;
