import React, { useState } from "react";

const LeagueInput = () => {
  const [matches, setMatches] = useState([]);
  const [editIndex, setEditIndex] = useState(-1);

  const handleAddMatch = (e) => {
    e.preventDefault();
    console.log(e);
    const leagueName = e.target.elements.league.value;
    const newMatch = {
      team1: {
        name: e.target.elements.team1.value,
        score: parseInt(e.target.elements.score1.value),
      },
      team2: {
        name: e.target.elements.team2.value,
        score: parseInt(e.target.elements.score2.value),
      },
    };
    if (editIndex === -1) {
      setMatches([...matches, newMatch]);
    } else {
      setMatches([
        ...matches.slice(0, editIndex),
        newMatch,
        ...matches.slice(editIndex + 1),
      ]);
      setEditIndex(-1);
    }
    e.target.reset();
  };

  const handleDeleteMatch = (index) => {
    setMatches([...matches.slice(0, index), ...matches.slice(index + 1)]);
  };

  const handleEditMatch = (index) => {
    setEditIndex(index);
    const match = matches[index];
    document.getElementsByName("team1")[0].value = match.team1.name;
    document.getElementsByName("score1")[0].value = match.team1.score;
    document.getElementsByName("team2")[0].value = match.team2.name;
    document.getElementsByName("score2")[0].value = match.team2.score;
  };

  return (
    <div className="flex flex-col mt-10 mx-10">
      <h1 className="flex justify-center font-bold mb-5">
        Enter League Details
      </h1>
      <div className="mb-5 mx-20 flex items-center">
        <label className="flex  space-x-2 items-center">
          <span>League Name:</span>
          <input
            className="border-2 border-blue-400 rounded-md"
            type="text"
            name="league"
          />
        </label>
      </div>
      <form onSubmit={handleAddMatch}>
        <div className="flex flex-col p-10">
          <div>
            <div className="flex space-x-2 justify-between mx-10">
              <label className="flex  space-x-2">
                <span> Team 1:</span>
                <input
                  className="border-2 border-blue-400 rounded-md"
                  type="text"
                  name="team1"
                />
              </label>
              <label className="flex  space-x-2">
                <span> Score 1:</span>
                <input
                  className="border-2 border-blue-400 rounded-md"
                  type="number"
                  name="score1"
                />
              </label>
            </div>

            <div className="flex space-x-2 justify-between mx-10">
              <label className="flex  space-x-2">
                <span> Team 2:</span>
                <input
                  className="border-2 border-blue-400 rounded-md"
                  type="text"
                  name="team2"
                />
              </label>
              <label className="flex  space-x-2">
                <span> Score 2:</span>
                <input
                  className="border-2 border-blue-400 rounded-md"
                  type="number"
                  name="score2"
                />
              </label>
            </div>
          </div>
          <div className="flex space-x-3 justify-end mx-10">
            <button
              type="submit"
              className="bg-green-500 py-2 px-10 w-fit text-white font-semibold rounded-md mt-5"
            >
              {editIndex === -1 ? "Add" : "Update"}
            </button>
            <button
              type="button"
              onClick={() => setEditIndex(-1)}
              className="bg-gray-500 py-2 px-10 w-fit text-white font-semibold rounded-md mt-5"
            >
              Cancel
            </button>
          </div>
        </div>
      </form>
      {matches.length > 0 && (
        <>
          <h2>Matches:</h2>
          <table>
            <thead>
              <tr>
                <th>Team 1</th>
                <th>Score 1</th>
                <th>Team 2</th>
                <th>Score 2</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {matches.map((match, index) => (
                <tr key={index}>
                  <td>{match.team1.name}</td>
                  <td>{match.team1.score}</td>
                  <td>{match.team2.name}</td>
                  <td>{match.team2.score}</td>
                  <td>
                    <button
                      type="button"
                      onClick={() => handleEditMatch(index)}
                    >
                      Edit
                    </button>
                    <button
                      type="button"
                      onClick={() => handleDeleteMatch(index)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default LeagueInput;
