import React, { useState } from "react";

function App() {
  const [matches, setMatches] = useState([]);

  const handleAddMatch = (e) => {
    e.preventDefault();
    const newMatch = {
      team1: {
        name: e.target.elements.team1.value,
        score: parseInt(e.target.elements.score1.value),
      },
      team2: {
        name: e.target.elements.team2.value,
        score: parseInt(e.target.elements.score2.value),
      },
    };
    setMatches([...matches, newMatch]);
    e.target.reset();
  };

  return (
    <div>
      <form onSubmit={handleAddMatch}>
        <label>
          Team 1:
          <input type="text" name="team1" />
        </label>
        <label>
          Score 1:
          <input type="number" name="score1" />
        </label>
        <br />
        <label>
          Team 2:
          <input type="text" name="team2" />
        </label>
        <label>
          Score 2:
          <input type="number" name="score2" />
        </label>
        <br />
        <button type="submit">Add match</button>
      </form>
      <h2>Matches:</h2>
      <ul>
        {matches.map((match, index) => (
          <li key={index}>
            {match.team1.name} {match.team1.score} - {match.team2.name}{" "}
            {match.team2.score}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
