import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const BASE_URL = "http://localhost:3001";

function Leagues() {
  const [leagues, setLeagues] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchLeagues() {
      try {
        const response = await axios.get(BASE_URL + "/api/leagues");
        console.log(response);
        setLeagues(response.data);
      } catch (e) {
        console.log(e);
      } finally {
        setLoading(false);
      }
    }
    fetchLeagues();
  }, []);

  const generateRankingHandler = async (leagueId) => {};

  return (
    <div>
      <div class="flex flex-col">
        <div class="overflow-x-auto sm:mx-0.5 lg:mx-0.5">
          <div class="py-2 inline-block min-w-full sm:px-6 lg:px-8">
            <div class="overflow-hidden">
              <table class="min-w-full">
                <thead class="bg-gray-200 border-b">
                  <tr>
                    <th
                      scope="col"
                      class="text-sm font-medium text-gray-900 px-6 py-4 text-left text-center"
                    >
                      index
                    </th>
                    <th
                      scope="col"
                      class="text-sm font-medium text-gray-900 px-6 py-4 text-left"
                    >
                      League Name
                    </th>
                    <th
                      scope="col"
                      class="text-sm font-medium text-gray-900 px-6 py-4 text-left text-center"
                    >
                      Total Matches
                    </th>
                    <th
                      scope="col"
                      class="text-sm font-medium text-gray-900 px-6 py-4 text-left"
                    >
                      Action
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {loading ? (
                    <div>Loading...</div>
                  ) : (
                    <>
                      {leagues.length > 0 ? (
                        <>
                          {leagues.map((league, index) => {
                            return (
                              <tr
                                key={league._id}
                                class="bg-white border-b transition duration-300 ease-in-out hover:bg-gray-100"
                              >
                                <td class="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 text-center">
                                  {index + 1}
                                </td>
                                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                                  {league.name}
                                </td>
                                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap text-center">
                                  {league.matches.length}
                                </td>
                                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                                  <div>
                                    <Link
                                      to={league._id}
                                      className="text-grey-lighter font-bold py-1 px-3 rounded text-xs bg-blue hover:bg-green-dark"
                                    >
                                      Show Ranking
                                    </Link>
                                  </div>
                                  <Link
                                    to={league._id}
                                    className="text-grey-lighter font-bold py-1 px-3 rounded text-xs bg-red hover:bg-blue-dark"
                                  >
                                    View
                                  </Link>
                                </td>
                              </tr>
                            );
                          })}
                        </>
                      ) : (
                        <>No League Data Found</>
                      )}
                    </>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Leagues;
